def build_categories(data):
    char_meanings = {
        "숫자": ["一", "二", "三", "四", "五", "六", "七", "八", "九", "十", "百", "千", "萬", "億"],
        "자연": ["日", "月", "火", "水", "木", "金", "土", "山", "川", "江", "海", "天", "地", "花", "草", "春", "夏", "秋", "冬", "風", "雪", "雨", "雲"],
        "사람/가족": ["人", "父", "母", "子", "女", "男", "兄", "弟", "祖", "孝", "童", "孫", "士", "兒"],
        "방향/위치": ["東", "西", "南", "北", "上", "下", "左", "右", "中", "內", "外", "前", "後"],
        "학교/학문": ["學", "校", "敎", "文", "字", "問", "記", "算", "數", "書", "讀", "題", "史"],
        "생활/일상": ["食", "休", "安", "住", "場", "市", "工", "農", "電", "家", "室", "門", "道", "路", "里", "村", "洞", "會", "社", "店"],
        "기타": []
    }
    char_to_idx = {e["character"]: i for i, e in enumerate(data)}
    categories = {}
    all_categorized = set()
    for cat, chars in char_meanings.items():
        if cat == "기타":
            continue
        categories[cat] = []
        for c in chars:
            if c in char_to_idx:
                idx = char_to_idx[c]
                if idx not in all_categorized or cat not in ["기타"]:
                    categories[cat].append(idx)
                    all_categorized.add(idx)
    categories["기타"] = [i for i in range(len(data)) if i not in all_categorized]
    return {k: sorted(v) for k, v in categories.items() if v}


def main():
    data = load_grade6()
    for char, meaning, sound, stroke_count, words in GRADE5_NEW_DATA:
        entry = {
            "character": char,
            "meaning": meaning,
            "sound": sound,
            "strokeCount": stroke_count,
            "words": [{"hanja": w[0], "reading": w[1], "meaning": w[2]} for w in words]
        }
        data.append(entry)
    categories = build_categories(data)
    result = {"version": "0.7", "data": data, "categories": categories}
    with open("default5.json", "w", encoding="utf-8") as f:
        json.dump(result, f, ensure_ascii=False, indent=2)
    print(f"default5.json 생성 완료: {len(data)}자")
    print(f"카테고리: {list(categories.keys())}")


if __name__ == "__main__":
    main()
